using Humanizer;
using Pizzeria.Models;
namespace Pizzeria.Data;

public class DbInitalizer
{
    public static void Initialize(PizzaDbContext context)
    {
        if (context.Pizzas!.Any()){
            return; // DB has been seeded
        }

        var pizzas = new Pizza[]
        {
            new Pizza
            {
                Name = "Margarita",
                Description = "The classic pizza",
                ImageUrl = "https://ocdn.eu/images/pulscms/MmE7MDA_/06d3b49c0634ba5de867c761bb0cdaf4.jpeg",
                Ingredients = "Tomato sauce, cheese, mozzarella, oregano, basil",
                Price = 37
            },
            
            new Pizza
            {
                Name = "Pepperoni",
                Description = "The classic pizza with pepperoni",
                ImageUrl = "https://www.telepizza.pl/upload/files/filename_pepperoni.jpg",
                Ingredients = "Tomato sauce, cheese, mozzarella, oregano, basil, pepperoni",
                Price = 38
            },
            
            new Pizza
            {
                Name = "Hawaiian",
                Description = "The classic pizza with ham and pineapple",
                ImageUrl = "data:image/jpeg;base64,",
                Ingredients = "Tomato sauce, cheese, pineapple, ham",
                Price = 40
            }
            
            
        };

        context.Pizzas!.AddRange(pizzas);
        context.SaveChanges();

    }
}